import React from "react";
import NavBarAction from "../components/NavBarAction";
import "./Equipment.less";

import on from "../assets/images/on.png";
import off from "../assets/images/off.png";
import SlotBox from "../components/SlotBox";

const Equipment = function Equipment(): JSX.Element {
  const controlData = [
    {
      id: 1,
      status: 1,
      title: "摄像头",
    },
    {
      id: 2,
      status: 0,
      title: "补光灯",
    },
    {
      id: 3,
      status: 1,
      title: "传感器",
    },
    {
      id: 4,
      status: 0,
      title: "排气扇",
    },
    {
      id: 5,
      status: 1,
      title: "灌溉器",
    },
  ];

  return (
    <div className="equipment-box">
      <NavBarAction title="设备管理" />
      <div className="content">
        <div className="data">
          <SlotBox>
            <>
              <div className="dataItem">
                <p>
                  <span className="num">65</span>个
                </p>
                <p>设备总数</p>
              </div>
              <div className="dataItem">
                <p>
                  <span className="num">64</span>个
                </p>
                <p>设备正常数</p>
              </div>
              <div className="dataItem">
                <p>
                  <span className="num">1</span>个
                </p>
                <p>设备异常数</p>
              </div>
            </>
          </SlotBox>
        </div>
        <div className="control">
          <SlotBox title="设备管理">
            <div className="controlItems">
              {controlData.map((item) => {
                return (
                  <div className="controlItem" key={item.id}>
                    <img src={item.status === 1 ? on : off} alt="" />
                    <p>{item.title}</p>
                  </div>
                );
              })}
            </div>
          </SlotBox>
        </div>
        <div className="status"></div>
      </div>
    </div>
  );
};

export default Equipment;
